mapper.select:
    select 
    ,
    	v_sys_region.preg_id,
		v_sys_region.reg_name,
		v_sys_region.prv_id,
		v_sys_region.reg_state,
		v_sys_region.preg_name,
		v_sys_region.prv_name
    from twr_rentinformationtower INNER JOIN v_sys_region ON v_sys_region.reg_id = twr_rentinformationtower.reg_id 


    		and twr_rentinformationtower.check_state = #{checkState}


			and twr_rentinformationtower.reg_id = #{regId,jdbcType=VARCHAR}


    		and v_sys_region.preg_id = #{pregId,jdbcType=VARCHAR}


    		and v_sys_region.prv_id = #{prvId,jdbcType=VARCHAR}

select rentinformation_id,business_confirm_number,tower_station_code
from twr_rentinformation
where business_confirm_number = #{businessConfirmNumber} 
and tower_station_code = #{towerStationCode}
and end_date >= #{activeDate} 
and start_date <=  #{activeDate}
