mapper.select:
  	select  

  	from 
  	twr_rentinformationtower_stopserver INNER JOIN v_sys_region ON v_sys_region.reg_id = twr_rentinformationtower_stopserver.reg_id


			and twr_rentinformationtower_stopserver.reg_id = #{regId,jdbcType=VARCHAR}


    		and v_sys_region.preg_id = #{pregId,jdbcType=VARCHAR}


    		and v_sys_region.prv_id = #{prvId,jdbcType=VARCHAR}
