mapper.select:
select

	distinct


from dat_supplier ds




	AND exists (SELECT vp.supplier_id FROM v_ele_billamount_payment vp
	WHERE vp.auditing_state = 0 and vp.supplier_id=ds.supplier_id AND
	billamount_id IS NULL


		AND contract_code LIKE CONCAT('%,', #{contractCode}, ',%')


		AND amount_type = #{amountType}


		AND payment_method = #{paymentMethod}


		AND billamount_date = #{billamountDate}


		AND reg_id = #{regId}


		AND reg_id IN

			#{listItem}



	)


	order by ${orderByClause}

select

	distinct


from dat_supplier


		and supplier_code = #{supplierCode,jdbcType=VARCHAR}


		and supplier_state = #{supplierState,jdbcType=VARCHAR}


		and (reg_id = #{regId,jdbcType=VARCHAR}

			or (is_downshare = #{isDownShare} and (reg_id IS NULL or reg_id = '') and
			preg_id in

				#{item}

			)


			or (prv_id = #{prvId} and is_downshare =
			#{isDownShare} and (preg_id IS NULL or preg_id = ''))

		)


		and ((preg_id = #{pregId,jdbcType=VARCHAR}
		or (preg_id = #{pregId,jdbcType=VARCHAR} and is_downshare =
		#{isDownShare}  ))

			or (prv_id = #{prvId} and is_downshare =
			#{isDownShare} and (preg_id IS NULL or preg_id = ''))

		)


		and prv_id = #{prvId,jdbcType=VARCHAR}


		and ( data_from = #{dataFrom,jdbcType=VARCHAR}

			or data_from = #{dataFrom1,jdbcType=VARCHAR}

		)


		and (supplier_name like CONCAT('%',#{supplierReg,jdbcType=VARCHAR},'%')
		or supplier_code like
		CONCAT('%',#{supplierReg,jdbcType=VARCHAR},'%'))

	and (1=1


			and (prv_id = #{prvId} and is_downshare =
			#{isDownShare} and (preg_id IS NULL or preg_id = ''))


			or reg_id in

				#{item}



			or (is_downshare = #{isDownShare}  and (reg_id IS NULL or reg_id = '') and
			preg_id in

				#{item}

			)


	)
