mapper.update:
update grp_datacollecttype_prv
set datacollect_prv_id = #{datacollectPrvId,jdbcType=VARCHAR},
  datacollecttype_id = #{datacollecttypeId,jdbcType=VARCHAR},
  datacollecttype_prv_filepath = #{datacollecttypePrvFilepath,jdbcType=VARCHAR},
  datacollecttype_prv_state = #{datacollecttypePrvState,jdbcType=INTEGER}
where datacollecttype_prv_id = #{datacollecttypePrvId,jdbcType=VARCHAR}

mapper.delete:
delete from grp_datacollecttype_prv
where grp_datacollecttype_prv.datacollect_prv_id in (
	select grp_datacollect_prv.datacollect_prv_id
	from grp_datacollect_prv
	where datacollect_id = #{datacollectId}
)

delete from grp_datacollecttype_prv
where grp_datacollecttype_prv.datacollecttype_id = #{datacollecttypeId}
and grp_datacollecttype_prv.datacollect_prv_id in (
	select grp_datacollect_prv.datacollect_prv_id
	from grp_datacollect_prv
	where datacollect_id = #{datacollectId}
)

mapper.select:
select datacollecttype_prv_filepath
from grp_datacollecttype_prv
where datacollect_prv_id = #{datacollectPrvId}
