mapper.select:
    select 

    from twr_towerbillbalance tb 
	INNER JOIN v_sys_region reg on reg.reg_id = tb.operator_reg_id


				and tb.data_type = #{dataType}


				and tb.business_confirm_number = #{businessConfirmNumber}


				and (tb.tower_station_code like CONCAT('%,', #{towerStationName}, ',%')
					or tb.tower_station_name like CONCAT('%,', #{towerStationName}, ',%')
				)



				and tb.account_peroid = #{accountPeroid}


				and tb.operator_reg_id = #{operatorRegId}


				and reg.prv_id = #{prvId}


				and reg.preg_id = #{pregId}


				and reg.towerbillbalance_id in

		            #{item}

    select 

    from twr_towerbillbalance tb 
    INNER JOIN v_sys_region reg on reg.reg_id = tb.operator_reg_id


			and tb.data_type = #{dataType}


			and tb.business_confirm_number = #{businessConfirmNumber}


			and ( INSTR(tb.tower_station_code,#{towerStationName})
				or INSTR(tb.tower_station_name,#{towerStationName})
				)


			and tb.account_peroid = #{accountPeroid}


			and tb.operator_reg_id = #{operatorRegId}






			and tb.towerbillbalance_id in

	            #{item}

    select tb.business_confirm_number,tb.tower_station_code,tb.account_peroid
    from twr_towerbillbalance as tb 


			and tb.data_type = #{dataType}


			and tb.business_confirm_number = #{businessConfirmNumber}


			and ( INSTR(tb.tower_station_code,#{towerStationName})
				or INSTR(tb.tower_station_name,#{towerStationName})
				)


			and tb.account_peroid = #{accountPeroid}


			and tb.operator_reg_id = #{operatorRegId}

select towerbillbalance_id
from twr_towerbillbalance
where account_peroid = #{accountPeroid}
and tower_station_code = #{towerStationCode}
and data_type = #{dataType}
