mapper.update:
update twr_rentinformationtower
set check_state = #{checkState}
where rentinformationtower_id = #{rentinformationtowerId}

mapper.select:
    select 
    ,
    	v_sys_region.preg_id,
		v_sys_region.reg_name,
		v_sys_region.prv_id,
		v_sys_region.reg_state,
		v_sys_region.preg_name,
		v_sys_region.prv_name
    from twr_rentinformationtower INNER JOIN v_sys_region ON v_sys_region.reg_id = twr_rentinformationtower.reg_id 

    	1=1

    		and rentinformationtower_id = #{rentinformationtowerId,jdbcType=VARCHAR}


    		and check_state = #{checkState}


			and v_sys_region.reg_id = #{regId,jdbcType=VARCHAR}



    		and preg_id = #{pregId,jdbcType=VARCHAR}


			and rentinformationtower_id in

	            #{item}    




        	and twr_rentinformationtower.rentinformationtower_id in  

                #{item.businessId}
